create table splits
(
    tx_guid         varchar(36)  not null,
    account_guid    varchar(36)  not null,
    memo            varchar(255) null,
    value_num       bigint       not null,
    value_denom     bigint       not null,
    reconcile_state char         null,
    guid            varchar(36)  not null
        primary key,
    created_at      datetime     null,
    updated_at      datetime     null,
    constraint splits_ibfk_1
        foreign key (account_guid) references accounts (guid),
    constraint splits_ibfk_2
        foreign key (tx_guid) references transactions (guid)
            on delete cascade
);

create index account_guid
    on splits (account_guid);

create index tx_guid
    on splits (tx_guid);

